import React from 'react';
import './styles/BadgeDetails.css';
import conflog from '../images/platziconf-logo.svg';
import PageLoading from '../components/PageLoading';
import Page from '../components/PageLoading';
import PageError from '../components/PageError';
import api from '/api'

class BadgeDetails extends React.Component {
    state = {
        loading: true,
        error: null,
        data: undefined,
    };
    componentDidMount() {
        this.fetchData();
    }

    fetchData = async () => {
        this.setState({ loading: true, error: null });
        try {
            const data = await api.badges.read(this.props.match.params.badgeId); //obtengo los datos para colocar el nameid 
            this.setState({ loading: false, data: data });
        } catch (error) {
            this.setState({ loading: false, error: error })
        }
    }

    render() {
        if (this.state.loading)
            return <PageLoading></PageLoading>
        if (this.state.error)
            return <PageError error={this.state.error} />
        return (
            <div>
                <div className='BadgeDetails__hero'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-6'>
                                <img src={conflog} alt='logo conf' />
                            </div>
                            <div className='col-6 BadgeDetails__hero-attendant-name'>
                                <h1>{this.state.data.firstName} {this.state.data.firstName}</h1>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
};

export default BadgeDetails;
