            <h1>NEW ATTENDANT</h1>
            <form>
                <div className='form-group'>
                    <label htmlFor=''>First Name</label>
                    <input onChange={this.handle_event} className='form-control' type='text' name='firstname'></input>
                </div>
                <buttom className='btn btn-primary'>Save</buttom>
            </form>
        </div>)
    }
}
export default BadgeForm;
