import React from 'react';


class BadgeForm extends React.Component {
    handle_change = e => {
        /*console.log({
            name: e.target.name,
            value: e.target.value
        })
        */
        //setstate me permite poder capturar y gguardar la inofrmacion del input o objeto fritsname toma como 1er parametrp el objeto a guradra osea ne este caso firstname
        this.setState({
            //firstname: e.target.value, // hastaaqui solo tenemos un state para el campo firstname si dejamos esto asi todos los campos restantes se guardaran en el campo firstname y no quiero eso, quiero que cada campo tenga su propio state!
            [e.target.name]: e.target.value, // aqui soluciono el problema anterior es un dict()
        })

    }
    handle_click = e => {
        console.log('click ya!')
    }
    handle_sub = e => {
        e.preventDefault(); // prevengo que le form se refresco y me borre los datos despÃºes de hacer click!
        console.log('from was sunmit!')
    }
    render() {
        return (<div>
            <h1>NEW ATTENDANT</h1>
            <form onSubmit={this.handle_sub}>
                <div className='form-group'>
                    <label htmlFor=''>First Name</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='firstname'></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Last Name</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='lastname'></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Email</label>
                    <input onChange={this.handle_change} className='form-control' type='email' name='email'></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Job Title</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='jobtitle'></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Twitter</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='twitter'></input>
                </div>
                <buttom onClick={this.handle_click} className='btn btn-primary'>Save</buttom>
            </form>
        </div>)
    }
}
export default BadgeForm;
