import React from 'react';
import './styles/Badges.css';
import logotipo from '../images/badge-header.svg';
import BadgesList from '../components/BadgeList';
import Pageloading from '../components/Pageloading';
import SkeletonLine from '../components'
import { Link } from 'react-router-dom';
import api from '../api';

class Badges extends React.Component {
    state = {
        loading: true,
        data: undefined,
        error: null,
    };
    componentDidMount() {
        this.obtener_personajes()
    }
    obtener_personajes = async () => {
        this.setState({ loading: true, error: null })
        try {
            const data = await api.badges.list();
            this.setState({ loading: false, data: data })
        } catch (error) {
            this.setState({ loading: false, error: error })
        }
    }
    render() {
        if (this.state.loading) {
            return (<SkeletonLine translucent={true} />);
        }
        return (
            <React.Fragment>
                <div className='Badges'>
                    <div className='Badges__hero'>
                        <div className='Badges__container'>
                            <img className='Badges_conf-logo' src={logotipo} alt='conf_logo'></img>
                        </div>
                    </div>
                </div>
                <div className='Badges__container'>
                    <div className='Badges__buttons'>
                        <Link to='/badges/new' className='btn btn-primary'>New Badge</Link>
                    </div>
                </div>
                <div className='BadgesList'>
                    <div className='Badges__container'>
                        <BadgesList badges={this.state.data}></BadgesList>
                    </div>
                </div>
            </React.Fragment>
        );
    }
};

export default Badges;
