import React from 'react';
import Navbar from '../components/Navbar'
import logo from '../images/badge-header.svg'
import './styles/badge_new.css'
import Badge from '../components/badge'
import BadgeForm from '../components/badgeform'


class BadgeNew extends React.Component {
    state = { form: {} };
    /*
    si dejmaos asi nuestro codigo nos daremos cuenta que el form sobrescrbira los inputs que vayamos rellenando
    osea nos tomara 1 or 1
    handleSubmit = e => {
        this.setState({
            form: {
                [e.target.name]: e.target.value, 
            }
        })
    };*/

    handleSubmit = e => {
        this.setState({
            form: {
                [e.target.name]: e.target.value, 
            }
        })
    };
    render() {
        return (
            <div>
                <Navbar />
                <div className='BadgeNew__hero'>
                    <img className='img-fluid' src={logo} alt='hero_logo' />
                </div>
                <div className='container'>
                    <div className='row'>
                        <div className='col-6'>
                            <Badge firstName='Daniela' lastName='Morales' twitter='Daniela10716033' jobTitle='Developer Full Stack' />
                        </div>
                        <div className='col-6'>
                            <BadgeForm onChange={this.handleSubmit} />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default BadgeNew;
