import React from 'react';
/*

NOTA IMPORTANTE: SetState funciona para ESCRIBIR ESTADOS
y state solito sirve para poder LEER ESTADOS

CADA UNO DE NUESTROS INPUTS HASTA EL MOMENTO SOLO TITNE UN TAG name MEJOR DICHO HAY 2 FUENTESDE DE INFO QUE TENDREMOS QUE MANEJRA Y HACER ESTO
NO E SMUY LEGIBLE ES DECIR TENEMOS LA COPIA DEL INPUT Y DEL SETSTATE PARA ARREGLAR ESTO NO VAMOS A VOLVER HACER UN SETSTATE
PARA CADA INPIT ES DECIR SOLO VAMSO A LEER EL STATE QUE YA TOMA EL SETSTATE LO SOLUCIONAMOS ASI:

1) TOMANDO CADA INPUT Y AGREGARLE UN TAG value={this.state.FirstName} 
y asi sucesivamnete para cada inpit modificando claramente el tag name


OTRA NOTA: TypeError: Cannot read property 'firstname' of null, CUANDO SE GENERA ESTE TIPO DE ERROR ES PORQUE NO HE
INICIALIZADO MI state para solucionar esto declaro mi state como un objeto vacio;
y por buenas praticas hare esto como 1ra linea de código dentro de mi bloque

*/

class BadgeForm extends React.Component {
    handle_change = e => {
        /*console.log({
            name: e.target.name,
            value: e.target.value
        })
        */
        //setstate me permite poder capturar y gguardar la inofrmacion del input o objeto fritsname toma como 1er parametrp el objeto a guradra osea ne este caso firstname
        this.setState({
            //firstname: e.target.value, // hastaaqui solo tenemos un state para el campo firstname si dejamos esto asi todos los campos restantes se guardaran en el campo firstname y no quiero eso, quiero que cada campo tenga su propio state!
            [e.target.name]: e.target.value, // aqui soluciono el problema anterior es un diccionario donde key=name y value=input de cada name
        })


    }
    handle_click = e => {
        console.log('click ya!')
    }
    handle_sub = e => {
        e.preventDefault(); // prevengo que le form se refresco y me borre los datos despúes de hacer click!
        console.log('from was sunmit!')
    }
    render() {
        return (<div>
            <h1>NEW ATTENDANT</h1>
            <form onSubmit={this.handle_sub}>
                <div className='form-group'>
                    <label htmlFor=''>First Name</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='firstname' value={this.state.firstname}></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Last Name</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='lastname' value={this.state.lastname}></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Email</label>
                    <input onChange={this.handle_change} className='form-control' type='email' name='email' value={this.state.email}></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Job Title</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='jobtitle' value={this.state.jobtitle}></input>
                </div>
                <div className='form-group'>
                    <label htmlFor=''>Twitter</label>
                    <input onChange={this.handle_change} className='form-control' type='text' name='twitter' value={this.state.twitter}></input>
                </div>
                <buttom onClick={this.handle_click} className='btn btn-primary'>Save</buttom>
            </form>
        </div>)
    }
}
export default BadgeForm;
