import React from 'react';


class BadgeForm extends React.Component {
    handle_event = e => {
        console.log({
            name: e.target.name,
            value: e.target.value
        })
    }
    handle_click = e => {
        console.log('click ya!')
    }
    handle_sub = e => {
        e.preventDefault(); // prevendo que le form se refresco y me borre los datos depsues de hacer click!
        console.log('from was sunmit!')
    }
    render() {
        return (<div>
            <h1>NEW ATTENDANT</h1>
            <form onSubmit={this.handle_sub}>
                <div className='form-group'>
                    <label htmlFor=''>First Name</label>
                    <input onChange={this.handle_event} className='form-control' type='text' name='firstname'></input>
                </div>
                <buttom onClick={this.handle_click} className='btn btn-primary'>Save</buttom>
            </form>
        </div>)
    }
}
export default BadgeForm;
