import React from 'react'
import './styles/Badges.css'
import logotipo from '../images/badge-header.svg'
import BadgesList from '../components/BadgeList'
import { Link } from 'react-router-dom'

class Badges extends React.Component {
    state = undefined


    constructor(props) {
        super(props);
        console.log('1 constructor')
        this.state = {
            data: []

        }
    }
    componentDidMount() {
        console.log('3 comoponetdidmount')
        this.time_out_id = setTimeout(() => {
            this.setState({
                data: [
                    {
                        "id": '234567890',
                        "firstName": 'Daniela',
                        "lastName": 'Morales',
                        "email": 'dmbmora@outlook.com',
                        "jobTitle": 'Developer',
                        "twitter": '@dani_mb',
                        "avatarUrl": 'https://img.icons8.com/bubbles/50/000000/lock-male-user.png',
                    },
                    {
                        "id": '23456789067890',
                        "firstName": 'Daniel',
                        "lastName": 'Mora',
                        "email": 'dmbmora123@outlook.com',
                        "jobTitle": 'Scrum master',
                        "twitter": '@daniel_mb',
                        "avatarUrl": 'https://img.icons8.com/bubbles/100/000000/check-female.png',
                    },
                    {
                        "id": '234567890678906789',
                        "firstName": 'Dylan',
                        "lastName": 'Morros',
                        "email": 'dmbmora123ert@outlook.com',
                        "jobTitle": 'developer expert',
                        "twitter": '@dylan_mb',
                        "avatarUrl": 'https://img.icons8.com/bubbles/100/000000/lock-female-user.png',

                    },
                    {
                        "id": '23456789067890456789',
                        "firstName": 'Dana',
                        "lastName": 'Mora',
                        "email": 'dmbmora123345@outlook.com',
                        "jobTitle": 'developer full stack',
                        "twitter": '@dana_mb',
                        "avatarUrl": 'https://img.icons8.com/bubbles/100/000000/see-male-account.png',

                    },
                ]
            })
        }, 3000)
    }
    componentDidUpdate(prevprops, prevstate) {
        console.log('5 updatecomponet')
        console.log({
            prevprops: prevprops, prevstate: prevstate
        });
        console.log({ state: this.state, props: this.props })
    }
    componentWillUnmount() {
        console.log('6. estoy saliendo de escena!')
        clearTimeout(this.time_out_id); /*le paso el id que ya expiró para que no me genere el error de actualizar un unmount */
        console.log(this.time_out_id)
    }
    render() {
        console.log('2 render')
        return (
            <React.Fragment>
                <div className='Badges'>
                    <div className='Badges__hero'>
                        <div className='Badges__container'>
                            <img className='Badges_conf-logo' src={logotipo} alt='conf_logo'></img>
                        </div>
                    </div>
                </div>
                <div className='Badges__container'>
                    <div className='Badges__buttons'>
                        <Link to='/badges/new' className='btn btn-primary'>New Badge</Link>
                    </div>
                </div>
                <div className='BadgesList'>
                    <div className='Badges__container'>
                        <BadgesList badges={this.state.data}></BadgesList>
                    </div>
                </div>
            </React.Fragment>
        );
    }
};

export default Badges;
